    private void cargarDB(){
        try { tabla.getItems().setAll(dao.listar()); pie.setText("Proveedores cargados desde base de datos."); }
        catch(Exception ex){ new Alert(Alert.AlertType.ERROR,"Error cargando proveedores: "+ex.getMessage()).showAndWait(); pie.setText("Error al cargar proveedores."); }
    }
    public BorderPane getRoot(){ return root; }
    private void mostrarProveedoresIncompletos() {
        try {
            java.util.List<String> lista = dao.listarIncompletos();
            String msg = lista.isEmpty()
                ? "No hay proveedores con entregas incompletas"
                : String.join(System.lineSeparator(), lista);
            new Alert(Alert.AlertType.INFORMATION, msg).showAndWait();
            pie.setText("Incompletos: " + lista.size());
        } catch (Exception ex) {
            new Alert(Alert.AlertType.ERROR, "Error consultando proveedores: " + ex.getMessage()).showAndWait();
        }
    }
